client/src/components/ui/badge.tsx.content:
import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  // Whitespace-nowrap: Badges should never wrap.
  "whitespace-nowrap inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2" +
  " hover-elevate " ,
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground shadow-xs",
        secondary: "border-transparent bg-secondary text-secondary-foreground",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground shadow-xs",

        outline: " border [border-color:var(--badge-outline)] shadow-xs",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  );
}

export { Badge, badgeVariants }

client/src/components/ServiceCard.tsx.content:
import { Card } from "@/components/ui/card";
import { LucideIcon } from "lucide-react";
import { useState } from "react";

interface ServiceCardProps {
  icon: LucideIcon;
  title: string;
  description: string;
  color?: "blue" | "green" | "yellow" | "red";
}

export default function ServiceCard({ icon: Icon, title, description, color = "blue" }: ServiceCardProps) {
  const [isHovered, setIsHovered] = useState(false);

  const colorClasses = {
    blue: {
      bg: "from-primary/10 to-primary/5",
      icon: "text-primary",
      glow: "group-hover:shadow-primary/20"
    },
    green: {
      bg: "from-brand-green/10 to-brand-green/5",
      icon: "text-brand-green",
      glow: "group-hover:shadow-brand-green/20"
    },
    yellow: {
      bg: "from-brand-yellow/10 to-brand-yellow/5",
      icon: "text-brand-yellow",
      glow: "group-hover:shadow-brand-yellow/20"
    },
    red: {
      bg: "from-destructive/10 to-destructive/5",
      icon: "text-destructive",
      glow: "group-hover:shadow-destructive/20"
    },
  };

  return (
    <Card 
      className={`p-6 md:p-8 group transition-all duration-500 hover:scale-105 hover:shadow-2xl ${colorClasses[color].glow} h-full relative overflow-hidden`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      data-testid="card-service"
    >
      <div className={`absolute inset-0 bg-gradient-to-br ${colorClasses[color].bg} opacity-0 group-hover:opacity-100 transition-opacity duration-500`} />

      <div className="relative z-10 flex flex-col h-full">
        <div className={`p-5 rounded-2xl bg-gradient-to-br ${colorClasses[color].bg} w-fit mb-5 group-hover:scale-110 transition-transform duration-500`}>
          <Icon className={`h-7 w-7 md:h-9 md:w-9 ${colorClasses[color].icon} transition-transform duration-500 ${isHovered ? 'rotate-12' : ''}`} />
        </div>
        <h3 className="font-heading font-semibold text-xl md:text-2xl text-foreground mb-4 group-hover:text-primary transition-colors duration-300" data-testid="text-service-title">
          {title}
        </h3>
        <p className="text-muted-foreground flex-1 leading-relaxed" data-testid="text-service-description">
          {description}
        </p>
      </div>
    </Card>
  );
}

client/src/components/AwardsSection.tsx.content:
import { useEffect, useState } from "react";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Award, Trophy, Star, Medal, Crown, Gem, GraduationCap, Heart, Lightbulb } from "lucide-react";

export default function AwardsSection() {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          }
        });
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById("awards");
    if (element) observer.observe(element);

    return () => observer.disconnect();
  }, []);

  const awards = [
    {
      icon: GraduationCap,
      title: "Professor of Practice Award",
      organization: "ISTD & ISME â€“ Diploma in Professor of Practice",
      year: "2025",
      gradient: "from-blue-500 via-cyan-500 to-teal-500",
    },
    {
      icon: Lightbulb,
      title: "Aegis Graham Bell Innovation Award",
      organization: "Innovation in Health: Holistic Approaches to Healthy Weight Management (Rated 8/10)",
      year: "2025",
      gradient: "from-purple-500 via-pink-500 to-rose-500",
    },
    {
      icon: Award,
      title: "Inspiring Coach of the Year",
      organization: "Coaching Excellence Awards",
      year: "2023",
      gradient: "from-indigo-500 via-blue-500 to-cyan-500",
    },
    {
      icon: Crown,
      title: "Teaching Training Quality Excellence Award",
      organization: "Indian Quality Assurance Council (IQAC) and ISTD",
      year: "2022",
      gradient: "from-emerald-500 via-green-500 to-teal-500",
    },
    {
      icon: Trophy,
      title: "Mahatma Gandhi Samman",
      organization: "NRI Welfare Society of INDIA at House of Commons, London",
      year: "2022",
      gradient: "from-yellow-500 via-amber-500 to-orange-500",
    },
    {
      icon: Star,
      title: "Iconic Women Leader",
      organization: "Asia-GCC Awards",
      year: "2021",
      gradient: "from-violet-500 via-purple-500 to-fuchsia-500",
    },
    {
      icon: Medal,
      title: "Top 10 Women Leaders in Soft Skills",
      organization: "Women Leadership Summit",
      year: "2020",
      gradient: "from-rose-500 via-pink-500 to-red-500",
    },
    {
      icon: Gem,
      title: "Excellence in L&OD",
      organization: "ISTD",
      year: "2019",
      gradient: "from-fuchsia-500 via-purple-500 to-violet-500",
    },
  ];

  return (
    <section id="awards" className="py-24 md:py-32 bg-gradient-to-b from-slate-900 via-slate-950 to-slate-900 relative overflow-hidden">
      {/* Animated background grid */}
      <div className="absolute inset-0 bg-[linear-gradient(to_right,#1e293b_1px,transparent_1px),linear-gradient(to_bottom,#1e293b_1px,transparent_1px)] bg-[size:4rem_4rem] opacity-20" />
      <div className="absolute inset-0 bg-[radial-gradient(circle_800px_at_50%_-100px,#3b82f680,transparent)]" />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <div className={`text-center mb-20 transition-all duration-1000 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}>
          <div className="inline-flex items-center gap-2 px-5 py-2 bg-gradient-to-r from-yellow-500/20 to-orange-500/20 rounded-full border border-yellow-500/30 backdrop-blur-xl mb-8">
            <Trophy className="h-5 w-5 text-yellow-400" />
            <span className="text-sm font-bold text-yellow-300">Award-Winning Excellence</span>
          </div>

          <h2 className="font-heading font-black text-5xl md:text-6xl lg:text-7xl mb-6 text-white" data-testid="text-awards-title">
            Recognized for <span className="bg-gradient-to-r from-yellow-400 via-amber-400 to-orange-400 bg-clip-text text-transparent">Excellence & Impact</span>
          </h2>
          <p className="text-xl md:text-2xl text-slate-400 max-w-3xl mx-auto font-medium">
            Honored with prestigious awards for transformative leadership and coaching excellence
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {awards.map((award, index) => (
            <Card
              key={index}
              className={`p-8 group hover:scale-110 transition-all duration-500 bg-gradient-to-br ${award.gradient} border-0 shadow-2xl hover:shadow-[0_0_100px_rgba(251,191,36,0.8)] relative overflow-hidden ${
                isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
              }`}
              style={{ transitionDelay: `${index * 100}ms` }}
              data-testid={`card-award-${index}`}
            >
              {/* Shine effect */}
              <div className="absolute inset-0 bg-gradient-to-tr from-white/0 via-white/20 to-white/0 -translate-x-full group-hover:translate-x-full transition-transform duration-1000" />

              <div className="relative z-10">
                <div className="p-5 rounded-2xl bg-white/10 backdrop-blur-sm w-fit mb-5 group-hover:scale-125 group-hover:rotate-12 transition-all duration-500">
                  <award.icon className="h-10 w-10 text-white" />
                </div>
                <h3 className="font-heading font-bold text-2xl text-white mb-3 group-hover:scale-105 transition-transform duration-300" data-testid={`text-award-title-${index}`}>
                  {award.title}
                </h3>
                <p className="text-white/90 mb-4 leading-relaxed font-medium" data-testid={`text-award-org-${index}`}>
                  {award.organization}
                </p>
                <Badge className="bg-white/20 text-white border-0 font-bold text-sm hover:bg-white/30" data-testid={`badge-award-year-${index}`}>
                  ðŸ† {award.year}
                </Badge>
              </div>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
}

