@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 220 15% 12%;

  --border: 220 13% 85%;

  --card: 220 14% 98%;

  --card-foreground: 220 15% 12%;

  --card-border: 220 13% 92%;

  --sidebar: 220 14% 96%;

  --sidebar-foreground: 220 15% 12%;

  --sidebar-border: 220 13% 89%;

  --sidebar-primary: 210 85% 42%;

  --sidebar-primary-foreground: 210 85% 98%;

  --sidebar-accent: 220 12% 88%;

  --sidebar-accent-foreground: 220 15% 15%;

  --sidebar-ring: 210 85% 42%;

  --popover: 220 14% 94%;

  --popover-foreground: 220 15% 12%;

  --popover-border: 220 13% 87%;

  --primary: 210 100% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 280 80% 55%;

  --secondary-foreground: 0 0% 100%;

  --muted: 220 12% 92%;

  --muted-foreground: 220 15% 25%;

  --accent: 340 85% 55%;

  --accent-foreground: 0 0% 100%;

  --destructive: 0 85% 60%;

  --destructive-foreground: 0 0% 100%;

  --input: 220 13% 75%;
  --ring: 210 85% 42%;
  --chart-1: 210 100% 50%;
  --chart-2: 145 70% 45%;
  --chart-3: 48 100% 50%;
  --chart-4: 0 85% 60%;
  --chart-5: 280 80% 55%;

  --font-sans: Inter, sans-serif;
  --font-heading: Poppins, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(220 13% 15% / 0.02);
  --shadow-xs: 0px 2px 0px 0px hsl(220 13% 15% / 0.03);
  --shadow-sm: 0px 2px 0px 0px hsl(220 13% 15% / 0.03), 0px 1px 2px -1px hsl(220 13% 15% / 0.04);
